import React from "react";
import { auth, provider } from "../firebase";
import { signInWithPopup } from "firebase/auth";

function LoginPage() {
  const handleLogin = () => {
    signInWithPopup(auth, provider)
      .then((result) => {
        localStorage.setItem("user", JSON.stringify(result.user));
        window.location.href = "/dashboard";
      })
      .catch((error) => {
        alert("Login failed!");
        console.error(error);
      });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-white to-blue-50 flex flex-col animate-fadeIn">
      {/* Header */}
      <header className="w-full py-4 px-6 shadow-sm flex justify-between items-center bg-white/80 backdrop-blur-sm">
        <h1 className="text-xl font-bold text-gray-800 animate-slideInLeft">Excel Analytics Platform</h1>
        <button className="text-sm text-blue-600 hover:underline hover:text-blue-800 transition-colors">
          My Account
        </button>
      </header>

      {/* Main Content */}
      <main className="flex flex-col-reverse lg:flex-row flex-1 items-center justify-between px-6 lg:px-20 py-10">
        {/* Left content */}
        <div className="lg:w-1/2 space-y-6 text-center lg:text-left animate-slideInUp">
          <h2 className="text-4xl font-bold text-gray-800">
            Analyze Excel Files <span className="text-blue-600">Smarter</span>
          </h2>
          <p className="text-gray-600 text-lg">
            Upload, visualize, and generate powerful insights from your Excel data in seconds.
          </p>
          <button
            onClick={handleLogin}
            className="bg-blue-600 text-white px-6 py-3 rounded-md hover:bg-blue-700 hover:scale-105 shadow-md transition-all duration-300 ease-in-out"
          >
            Sign in with Google
          </button>
        </div>

        {/* Right image */}
        <div className="lg:w-1/2 mb-10 lg:mb-0 animate-slideInRight">
          <img
            src="https://via.placeholder.com/600x400?text=Analytics+Dashboard"
            alt="Dashboard Preview"
            className="w-full rounded-lg shadow-lg hover:scale-[1.02] transition-transform duration-300"
          />
        </div>
      </main>
    </div>
  );
}

export default LoginPage;
