import React from "react";
import { auth, provider } from "../firebase";
import { signInWithPopup } from "firebase/auth";

function LoginPage() {
  const handleLogin = () => {
    signInWithPopup(auth, provider)
      .then((result) => {
        localStorage.setItem("user", JSON.stringify(result.user));
        window.location.href = "/dashboard";
      })
      .catch((error) => {
        alert("Login failed!");
        console.error(error);
      });
  };

  return (
    <div className="min-h-screen bg-white flex flex-col">
      {/* Header */}
      <header className="w-full py-4 px-6 shadow-sm flex justify-between items-center border-b">
        <h1 className="text-lg font-semibold text-gray-700">Excel Analytics Platform</h1>
        <nav className="space-x-4">
          <a href="#" className="text-gray-600 hover:text-blue-600">Features</a>
          <a href="#" className="text-gray-600 hover:text-blue-600">Docs</a>
          <a href="#" className="text-gray-600 hover:text-blue-600">Support</a>
          <a href="#" className="text-gray-600 hover:text-blue-600">My Account</a>
        </nav>
      </header>

      {/* Main Content */}
      <main className="flex flex-col lg:flex-row items-center justify-between px-6 lg:px-20 py-10">
        {/* Left content */}
        <div className="lg:w-1/2 space-y-6 text-center lg:text-left">
          <h2 className="text-4xl font-bold text-gray-800">
            Analyze Excel Files <span className="text-blue-600">Smarter</span>
          </h2>
          <p className="text-gray-600 text-lg">
            Discover how our platform helps you upload, visualize, and generate powerful insights from your Excel data in less time.
          </p>
          <div className="space-x-4">
            <button
              onClick={handleLogin}
              className="bg-blue-600 text-white px-6 py-3 rounded-md hover:bg-blue-700 transition"
            >
              Sign In with Google
            </button>
            <button className="bg-white text-blue-600 px-6 py-3 rounded-md border border-blue-600 hover:bg-blue-50 transition">
              Learn More
            </button>
          </div>
        </div>

        {/* Right image */}
        <div className="lg:w-1/2 mt-10 lg:mt-0">
          <img
            src="https://via.placeholder.com/600x400?text=Analytics+Dashboard"
            alt="Dashboard Preview"
            className="w-full rounded-lg shadow-lg"
          />
        </div>
      </main>
    </div>
  );
}

export default LoginPage;
